import groovy.json.*

def flowFile = session.get()
if (!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    def inputJson = new JsonSlurper().parse(inputStream)
    def filtered = inputJson.tables.findAll { it.is_to_be_processed == true }
    def result = [tables: filtered]
    outputStream.write(new JsonBuilder(result).toPrettyString().getBytes('UTF-8'))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
